var conn=new WebSocket("ws://localhost:8080");conn.onopen=function(n){console.log("Connection established!")},conn.onmessage=function(n){for(var e=JSON.parse(n.data),o=0;o<e.length;o++){var t=e[o],s=document.getElementById("workshop-"+t.id);s.innerHTML="<span class='workshop-title'>"+t.titel+"</span>\n<span class='workshop-places'>"+t.plaatsen+"</span>\n"}},window.onload=function(){var n=document.getElementById("submit-btn");n.addEventListener("click",function(n){var e=document.getElementById("workshop-select"),o=e.options[e.selectedIndex].value;conn.send(o)})};